INSERT INTO `funfact` VALUES (16,'Each person creates about 4.7 pounds of waste every single day.','soda-can.jpg',0),(17,'In the US, 33.4% of solid waste is either recycled or composted.','',0),(5,'Recycling a ton of mixed paper saves the energy equivalent of 185 gallons of gasoline, or 4200 kwh of electricity.<br>','',0),(6,'Recycling a ton of mixed paper saves 7000 gallons of water over processing virgin resources.<br>','',0),(18,'Making a ton of paper from recycled paper saves up to 17 trees and uses 50% less water than creating paper from virgin pulp.','',0),(4,'Office workers generate 4 million tons of paper each year. If all were recycled, it would save 68 million trees and untold gallons of precious water.<br>','recycled-paper-1.jpg',0),(7,'One compactor holds approximately 5 tons of mixed paper, so what is stored in the compactor then processed saves 35,000 gallons of water and 21,000 kwh of electricity.<br>','',0),(8,'Recycling a ton of aluminum cans conserves the equivalent of 1,665 gallons of gasoline.<br>','',0),(9,'Recycling aluminum requires less than 5% of the energy required to make it from bauxite ore.<br>','',0),(10,'The energy you save by recycling a single aluminum can run a tv for 3 hours.<br>','',0),(11,'Over 80,000,000,000 aluminum soda cans are used every year.<br>','',0),(12,'Recycling 1 ton of plastic can save over 7 cubic yards of landfill space.<br>','',0),(13,'Plastics make up more than 12% of our trash.','',0),(14,'Americans use 2,500,000 plastic bottles every hour and most are not recycled.<br>','',0),(15,'An aluminum can takes 80-100 years to decompose in a landfill; plastics take up to 700 years.<br>','',0);
